import React from 'react';
import '../stylesheets/mainPage.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Signup from '../forms/signup';
import Login from '../forms/login';


class MainPage extends React.Component {

    state = {
        signup: true
    }

    changeSingupState = () => {
        this.setState({
            signup: !this.state.signup
        })
    }


    render() {
        return (
            <div className='main-page'>
                {this.state.signup ? <div style={{display:'flex', direction:'column'}}> <Signup /> <div className='attach-to-signup-form'> <h6>Member Already</h6> <button onClick={this.changeSingupState}>Login</button>}</div> </div> : 
                <div> <Login /> <div className='after-loginButtons' ><h6>New Member</h6> <button onClick={this.changeSingupState}>Sign Up</button> </div>} </div>}
            </div>
        )
    }
}


export default MainPage
