import React from 'react'
import ActivitiesContainer from '../containers/ActivitiesContainer'
{/* <ActivitiesContainer /> */}

const Category = (props) => {

   function renderActivities(){
    //    debugger
        let categoryActivities = props.activities.select(a => a.category_id === props.category.id)
        return <ActivitiesContainer activities={categoryActivities}/>
    }

    return(
        <div className='category'>
            <p> Category: {props.category.category_type} </p>
            <img onClick={renderActivities()} src={props.category.image} height='100px'></img>

        </div>
    )
}



export default Category
