import React, { useState, useEffect } from 'react'
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography'
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Chip from '@material-ui/core/Chip';
// import FaceIcon from '@material-ui/icons/Face';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';


const useStyles = makeStyles((theme) => ({
    root: {
        margin: '50px',
        padding: theme.spacing(3, 2)
    },
    flex: {
        display: 'flex',
        alignItem: 'center'
    },
    chatwindow: {
        width: '100%',
        height: '300px',
        padding: '20px'
    },
    chatbox: {
        width: '85%'
    },
    button: {
        width: '15%'
    },
}));

export default function ChatRoomT(props) {
    const classes = useStyles();

    const [textValue, changeTextValue] = React.useState('')
    const [chatroom, changeChatroom] = React.useState([])
    const [messages, changeMessages] = React.useState([])
    const [message, changeMessage] = React.useState([])

    useEffect(() => {
        fetch(`http://localhost:3000/chatrooms/${props.chatroom.id}`).then(resp => resp.json()).then(data => changeChatroom(data) && changeMessages(data.messages))

        // Creates the new websocket connection
        let socket = new WebSocket('ws://localhost:3000/cable');

        // When the connection is 1st created, this code runs subscribing the clien to a specific chatroom stream in the ChatRoomChannel
        socket.onopen = function (event) {
            console.log('WebSocket is connected.');

            const msg = {
                command: 'subscribe',
                identifier: JSON.stringify({
                    id: 1,
                    channel: 'ChatroomChannel'
                }),
            };

            socket.send(JSON.stringify(msg));
        };

        // When the connection is closed, this code is run
        socket.onclose = function (event) {
            console.log('WebSocket is closed.');
        };

        // When a message is received through the websocket, this code is run
        socket.onmessage = function (event) {
            const response = event.data;
            const msg = JSON.parse(response);

            // Ignores pings
            if (msg.type === "ping") {
                return;
            }

            console.log("FROM RAILS: ", msg);

            // Renders any newly created messages onto the page
            if (msg.message) {

            }

        };

        // When an error occurs through the websocket connection, this code is run printing the error message
        socket.onerror = function (error) {
            console.log('WebSocket Error: ' + error);
        };

    }, []);

    const postMessage = (event) => {
        event.preventDefault()
        let data = { content: message, chatroom_id: chatroom.id, user_id: props.currentUser.id }
        console.log('message', data);

        fetch('http://localhost:3000/messages', {
            method: 'Post',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then(response => {
            changeMessages([...messages, response])
        })
    }




    console.log(messages);

    return (
        <div>
            <Paper className={classes.root}>
                <Typography variant='h5' component='h5'>
                    Chat Room
                </Typography>
                {/* <Typography component='p'>
                    PP
                </Typography> */}
                <div className={classes.flex}>
                    <div className={classes.chatwindow}>
                        <List>
                            {
                                messages.map((msg, i) => (
                                    <div className={msg.flex} key={i}>
                                        <Chip label={msg.user_id} className={classes.chip} />
                                        <Typography variant='p'> {msg.content} </Typography>
                                    </div>
                                ))
                            }

                        </List>
                    </div>
                </div>
                <div className={classes.flex}>
                    <TextField
                        id="standard-multiline-flexible"
                        label="Send a message"
                        className={classes.chatbox}
                        value={message}
                        onChange={e => changeMessage(e.target.value)}
                    // multiline
                    // rowsMax={4}
                    />

                    <Button variant="contained" color="primary">
                        Send
                    </Button>

                    {/* <div className={classes.chatbox}>

                    </div>
                    <div className={classes.button}>

                    </div> */}
                </div>
            </Paper>
        </div>
    )
}
