import React from 'react';
import './App.css';
import { connect } from 'react-redux'
import { fetchCateogriesCreator, fetchActivitiesCreator } from './actionCreators/actionCreater'
import CateogriesContainer from './containers/categoriesContainer'


class App extends React.Component {

  state = {
    categories: [],
    activities: []
  }

  fetchCategories = () => {
    fetch('http://localhost:3000/categories').then(resp => resp.json())
      .then(data => {
        this.setState({ categories: data })
      })
  }

  fetchActivities = () => {
    fetch('http://localhost:3000/activities').then(resp => resp.json())
      .then(data => {
        this.setState({ activities: data })
      })
  }

  componentDidMount() {
    this.fetchCategories()
    this.fetchActivities()
  }

  render() {
    console.log(this.state);

    return (
      <div className="App">
        <CateogriesContainer categories={this.state.categories} />
      </div>
    );
  }

}

const msp = state => {
  return {
    categories: state.categories,
    activities: state.activities
  }
}

const mdp = dispatch => {
  return {

    fetchCategories: () => dispatch(fetchCateogriesCreator()),
    fetchActivities: () => dispatch(fetchActivitiesCreator()),
  }
}


export default connect(msp, mdp)(App)
