import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';
import Marker from 'google-map-react';


const AnyReactComponent = ({ text }) => <div>{text}</div>;



class Map extends Component {

  state = {
    lat: null,
    lng: null,
    zoom: 12,
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {}
  }

  // static defaultProps = {
  //   center: {
  //     lat: 0,
  //     lng: 0
  //   },
  //   zoom: 11
  // };

  componentDidMount() {
    fetch(`https://maps.googleapis.com/maps/api/geocode/json?address='${this.props.activity.address}'&key=AIzaSyD4X3Xez83U_L3WZm6Fny8zsSxN_G4s1a4`)
      .then(resp => resp.json())
      .then(data => this.setState({
        lat: data.results[0].geometry.location.lat, lng: data.results[0].geometry.location.lng
      }))
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };


  render() {

    return (
      <div style={{ height: '30vh', width: '50%' }}>
        {this.state.lat ?
          <GoogleMapReact
            bootstrapURLKeys={{ key: 'AIzaSyD4X3Xez83U_L3WZm6Fny8zsSxN_G4s1a4' }}

            defaultCenter={{ lat: this.state.lat, lng: this.state.lng }}
            defaultZoom={this.state.zoom}
          >
            {this.state.lat ?
              <div>
                <AnyReactComponent
                  lat={this.state.lat}
                  lng={this.state.lng}
                  text={this.props.activity.name}
                />
                {/* <Marker
                  onClick={this.onMarkerClick}
                  name={'Kenyatta International Convention Centre'}
                /> */}

              </div> :
              null}
            {/* <InfoWindow
              marker={this.state.activeMarker}
              visible={this.state.showingInfoWindow}
              onClose={this.onClose}
            >
              <div>
                <h4>{this.props.activity.name}</h4>
              </div>
            </InfoWindow> */}



          </GoogleMapReact>
          : null}

      </div>
    );
  }
}

export default Map;
