import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import { removeCurrentUser } from '../actionCreators/actionCreater'
import { connect } from 'react-redux'
import { useHistory } from "react-router-dom";
import Avatar from '@material-ui/core/Avatar';
import Hidden from 'material-ui/Hidden';
import Drawer from 'material-ui/Drawer';
import CustomIconButton from '../CustomAppBar/CustomIconButton/CustomIconButton';


const drawerWidth = 250;




const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
    avatars: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1),
        },
    },
}));

function NewNavBar(props) {
    let history = useHistory();
    const classes = useStyles();
    function logOut() {
        props.removeCurrentUser()
        localStorage.clear()
        history.push("/");
    }

    return (
        <div className={classes.root}>
            <AppBar position="static">
            <CustomIconButton
                    iconName='bars'
                    aria-label="open drawer"
                    onClick={this.handleDrawerToggle}
                    className={classes.navIconHide}>
                    <Hidden xlUp>
                        <Drawer
                            variant="temporary"
                            anchor='left'
                            open={this.state.open}
                            classes={{
                                paper: classes.drawerPaper
                            }}
                            onClose={this.handleDrawerToggle}
                            ModalProps={{
                                keepMounted: true
                            }}>
                            {drawer}
                        </Drawer>
                    </Hidden>
                </CustomIconButton>
                <Toolbar>
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" className={classes.title}>
                        MyActivities
                    </Typography>
                    {props.currentUser ?
                        <Avatar alt={props.currentUser.username} src={props.currentUser.image} onClick={logOut}/>:
                        <Button color="inherit">Login</Button>

                    }
                </Toolbar>
            </AppBar>
        </div>
    );
}
export default connect(null, { removeCurrentUser })(NewNavBar)
