import React from 'react'
import { connect } from 'react-redux'
import Chatroom from './chatroom'

class Activity extends React.Component {

    state = {
        name: '',
        activityType: '',
        image: '',
        about: '',
        date: new Date(),
        rating: 5,
        user_id: 1,
        category_id: 1,
        address: ''

    }

    joinActivity = () => { // creates a participant 
        let data = {user_id: this.props.currentUser, activity_id: this.props.activity.id}
        fetch('http://localhost:3000/participants', {
            method: 'Post',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then(resp => resp.json())
        .then(response => { if (response.errors) { alert(response.errors) } })
        }


    render() {
        return (
            <div className='activity'>
                <p>{this.props.activity.name}</p>
                {/* <Chatroom /> */}
                {this.props.activity.user_id !== this.props.currentUser.id ? <button onClick={this.joinActivity} >Join Activity</button> : null}
            </div>
        )
    }
}

const msp = state => {
    return {
        currentUser: state.currentUser
    }
}

export default connect(msp, null)(Activity)
