import React from 'react'
import { connect } from 'react-redux'

class ActivityPage extends React.Component {

    state = {
        activity: null
    }
    componentDidMount() {
        fetch(`http://localhost:3000/activities/${this.props.match.params.id}`)
            .then(resp => resp.json())
            .then(data => this.setState({ activity: data }))
    }

    render() {
        // console.log(this.state.activity.user);

        const {name, image, activityType, about, date, user_id, category_id, address} = this.state.activity
        return (
            <div>
                {this.state.activity ?
                    <div>
                        <h4>{name}</h4>
                        {/* <h4>{this.state.activity.user.name}</h4> */}

                        {/* <img src={}></img> */}
                    </div>
                    : <h1>"NO ACTIVITY HERE"</h1>}
            </div>
        )
    }
}


export default ActivityPage
