import React from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputBase from '@material-ui/core/InputBase';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { useHistory } from "react-router-dom";
import { assignCurrentUser } from '../actionCreators/actionCreater'
import { connect } from 'react-redux'





const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
}));

function AddActivityT(props) {
    let history = useHistory();
    const classes = useStyles();
    const [username, setusername] = React.useState('');
    const [password, setpassword] = React.useState('');
    const [city, setcity] = React.useState('');
    const [image, setimage] = React.useState('');

    const handleChange1 = (event) => {
        setusername(event.target.value);
    };
    const handleChange2 = (event) => {
        setpassword(event.target.value);
    };
    const handleChange3 = (event) => {
        setcity(event.target.value);
    };
    const handleChange4 = (event) => {
        setimage(event.target.value);
    };


    function handleSubmit(event) {

        event.preventDefault()
        let data = { username: username, password: password, image: image, city: city }
        fetch('http://localhost:3000/signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then(resp => resp.json()).then(response => {
            if (response.errors) { alert(response.errors) } else {
                props.assignCurrentUser(response)

                history.push("/home");
                localStorage.user_id = response.id
            }
        })
    }
    return (
        <div>
            <video id='video1' autoPlay muted loop >
                <source src='https://vod-progressive.akamaized.net/exp=1586929622~acl=%2A%2F1698360923.mp4%2A~hmac=2af0e496ee71df1e87710ab0b03e785c9df54655c43dc654abf2159d71998c66/vimeo-prod-skyfire-std-us/01/4710/15/398551000/1698360923.mp4' type='video/mp4' />
            </video>
            <FormControl className={classes.margin}>
                <TextField
                    required
                    id="standard-required"
                    label="Username"
                    placeholder='Username'
                    value={username}
                    onChange={handleChange1}
                />
                <TextField
                    id="standard-password-input"
                    label="Password"
                    type="password"
                    autoComplete="current-password"
                    value={password}
                    onChange={handleChange2}
                />
                <TextField
                    required
                    id="standard-required"
                    label="City"
                    placeholder='City'
                    value={city}
                    onChange={handleChange3}
                />
                <TextField
                    required
                    id="standard"
                    label="Image Url"
                    placeholder='Image Url'
                    value={image}
                    onChange={handleChange4}
                />
                <Button onClick={handleSubmit}>Signup</Button>
                <Button onclick={props.changeSingupState}>Already a Member</Button>
            </FormControl>


