import React from 'react'
import { connect } from 'react-redux'
import Activity from '../components/activity'
import { render } from '@testing-library/react'
import { Link } from 'react-router-dom'
import Search from '../components/search'

class ActivitiesPage extends React.Component {

    state = {
        searchTerm: '',
        activities: [],
    }


    filter = () => {

    }

    sort = () => {

    }
    searchTransaction = (event) => {
        this.setState({
          searchBar: event.target.value,
          searchedtransaction: this.state.transactions.filter(t=> t.description.includes(this.state.searchBar))

        })    
      }

      renderSearch = () => {
        this.setState({
          showTransactions: this.state.searchedtransaction
        })
      }


    searchActivities = event => {
        this.setState({
            [event.target.name]: event.target.value,
             activities: this.state.activities.filter(a => a.name.includes(this.state.searchTerm))
            })
    }


    renderActivtites = () => {
        let activitiesCopy = [...this.props.activities]
        return activitiesCopy.map((activity, index) => <Activity index={index} key={activity.id} activity={activity} />)
    }


    render() {
        return (
            <div>
                <Search searchActivities={this.searchActivities} renderSearch={this.renderSearch} />
                <div className='activities'>
                    <h1>ActivitiesPage</h1>
                    {this.state.activities ? this.renderActivtites() : null}
                </div>
            </div>

        )
    }
}

const msp = state => {
    return {
        activities: state.activities
    }
}

export default connect(msp)(ActivitiesPage)
