    handleClick = () => {
        this.setState({showActivities: !this.state.showActivities})
    }

     renderActivities = () => {
        //    debugger
            return this.props.activities.filter(a => a.category_id === this.props.category.id)

        }

        changeState = () => {
            this.setState({showActivities: !this.state.showActivities})
            // return <ActivitiesContainer activities={this.props.activities}/> 
        }
    render() {
        console.log(this.state.showActivities);

        return (
            <div className='category'>
                {this.state.showActivities && <ActivitiesContainer activities={this.props.activities}/>}
                    <p onClick={this.showActivities}>Category: {this.props.category.category_type} </p>
                    <img src={this.props.category.image} height='100px'></img>
                    <button onClick={this.handleClick}></button>

                </div>
        )

    }
}



export default Category
