import React from 'react'
import ActivitiesContainer from '../containers/ActivitiesContainer'
{/* <ActivitiesContainer /> */ }

class Category extends React.Component {


    handleClick = () => {
        this.setState({ showActivities: !this.state.showActivities })
    }

    renderActivities = () => {
        //    debugger
        return this.props.activities.filter(a => a.category_id === this.props.category.id)

    }

    // changeState = () => {
    //     this.setState({ showActivities: !this.state.showActivities })
    //     // return <ActivitiesContainer activities={this.props.activities}/> 
    // }
    render() {

        return (

                    <div className='category'>
                        <p onClick={this.props.changeView(this.props.category.id)}>Category: {this.props.category.category_type} </p>
                        <img src={this.props.category.image} height='100px'></img>
                        <button onClick={this.handleClick}></button>

                    </div>

        )

    }
}



export default Category
