    console.log('clicked');


  }
  const { color, name, id } = props;
console.log(props.activity);

  return (
    <div onClick={onClickMarker}>
      <div
        className="pin bounce"
        style={{ backgroundColor: color, cursor: 'pointer' }}
        title={''}
      />


        <div className="pulse" >
        {props.activity ? 
          <h2>{props.activity.name}</h2>
