import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';
import Marker from './marker.js'
import { useLocation } from 'react-router-dom';
// import InfoWindow from 'google-map-react';
// import Marker from 'google-maps-react';
// import {InfoWindow, Marker} from 'google-map-react';


const array = [{ lat: 40.764357, lng: -73.923462 }, { lat: 40.778790, lng: -73.906588 }, { lat: 40.744309, lng: -73.941860 }]

const BigMap = (props) => {

  // componentDidMount() {
  //   fetch(`https://maps.googleapis.com/maps/api/geocode/json?address='${this.props.activity.address}'&key=AIzaSyD4X3Xez83U_L3WZm6Fny8zsSxN_G4s1a4`)
  //     .then(resp => resp.json())
  //     .then(data => this.setState({
  //       lat: data.results[0].geometry.location.lat, lng: data.results[0].geometry.location.lng
  //     }))
  // }
  onMarkerClick = (event) => {
    console.log(event.target);

  }

  displayMarkers = () => {
    return array.map((place, index) => {
      return <Marker
        activity={this.props.activity}
        lat={place.lat}
        lng={place.lng}
        label={'activity'}
        name={'activity'}
        color='blue'
      // onClick={this.onMarkerClick}
      />
    })
  }

  mapMarkers = () => {
    // debugger
    return array.map(marker =>
      <Marker
        // key={marker.id}
        // onClick={onClick}
        position={{ lat: marker.lat, lng: marker.lng }}
      />
    )
  }

  return (
    <div style={{ height: '50vh', width: '100%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key: 'AIzaSyD4X3Xez83U_L3WZm6Fny8zsSxN_G4s1a4' }}
          defaultCenter={{ lat: 40.764357, lng: -73.923462 }}
          defaultZoom={13}
        >
          {/* {this.mapMarkers()} */}
          {/* <Marker
              activity = {this.props.activity}
              lat={this.state.lat}
              lng={this.state.lng}
              name={this.props.activity.name}
              color='blue'
              onClick={this.onMarkerClick}
            /> */}

          {/* {this.displayMarkers()} */}
        </GoogleMapReact>

    </div>
  );
}

export default BigMap;
