import React from 'react'
import AddActivity from '../forms/addActivity'
import Activity from '../components/activity'


class ActivitiesContainer extends React.Component {
    state = {
        addActivityButton: false
    }

    changeAddButtonState = () => {
        this.setState({addActivityButton: !this.state.addActivityButton})
    }
     renderActivities() {
        return this.props.activities.map(activitiy => <Activity activity={activitiy} key={activitiy.id}/>)
    }

    render(){
        return (
            <div>
                {this.renderActivities()}
                {this.state.addActivityButton ? <AddActivity /> : <button onClick={this.changeAddButtonState}>Add Activity</button>}
            </div>
        )

    }
}


export default ActivitiesContainer
