import React from 'react'
import { connect } from 'react-redux'
import ActivitiesContainer from '../containers/ActivitiesContainer'


class Category extends React.Component{

    renderActivities = () => {
        let activities =  this.props.activities.select(a => a.category_id === this.props.category.id)
    }
    render(){
        return(
            <div className='category'>
                <p> Category: {this.props.category.category_type} </p>
                <img src={this.props.category.image} height='100px'></img>
                <ActivitiesContainer />
            </div>
        )
    }
}

const msp = state => {
    return {
       activities: state.activities 
    }
}
export default connect(msp)(Category)
