import React from 'react';
import '../stylesheets/mainPage.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import SignupT from '../forms/signup2';
import LoginT from '../forms/login2';
import Button from '@material-ui/core/Button';
// import Login from '../forms/login';


class MainPage extends React.Component {

    state = {
        signupState: true
    }

    changeSingupState = () => {
        this.setState({
            signupState: !this.state.signupState
        })
    }


    render() {
        console.log(this.changeSingupState);

        return (
            <div className='main-page'>

                <video id='video1' autoPlay muted loop >
                    <source src='https://vod-progressive.akamaized.net/exp=1586945817~acl=%2A%2F1496305357.mp4%2A~hmac=425991dab0efb8df25c92c2af9debec59126bafa564d3058a7cfec226175a27a/vimeo-prod-skyfire-std-us/01/2724/14/363624829/1496305357.mp4' type='video/mp4' />
                </video>
                <div>
                    {this.state.signupState ? <div> <SignupT /> <button onclick={this.changeSingupState}>Already Member</button> </div> : <LoginT />}
                    {/* <button onclick={this.changeSingupState}>New Member</button> */}
                </div>
            </div>

        )
    }
}


export default MainPage


