import React from 'react'
import Activity from '../components/activity'
import '../../src/index.css'
import MoreButton from '../components/moreButton'


class ActivitiesContainer extends React.Component {

    state = {
        startIndex: 0
    }

    nextPage = () => {
        let newIndex = this.state.startIdx + 4;
        if (newIndex >= this.props.activities.length){ newIndex = 0 }
        this.setState({
          startIdx: newIndex
          //  startIdx: this.state.startIdx + 4 >= this.props.sushis.length ? 0 : this.state.startIdx + 4
        })
      }


    renderActivities = () => {
        let { startIdx } = this.state;

        let fourActivities = this.props.activities.slice(startIdx, startIdx + 4)
        return fourActivities.map(activitiy => <Activity activity={activitiy} key={activitiy.id} />)
    }

    render() {
        return (
            <div>
                <div className='belt'>
                    {this.renderActivities()}
                </div>
                <MoreButton nextPage={this.nextPage}/>
            </div>
        )
    }
}



export default ActivitiesContainer
