import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';


const AnyReactComponent = ({ text }) => <div>{text}</div>;



class Map extends Component {

  state = {
    lat: null,
    lng: null
  }

  static defaultProps = {
    center: {
      lat: 59.95,
      lng: 30.33
    },
    zoom: 11
  };

  componentDidMount() {
    fetch(`https://maps.googleapis.com/maps/api/geocode/json?address='${this.props.location}'&key=AIzaSyD4X3Xez83U_L3WZm6Fny8zsSxN_G4s1a4`)
      .then(resp => resp.json())
      .then(data => this.setState({
        lat: data.results[0].geometry.location.lat, lng: data.results[0].geometry.location.lng
      }))
  }


  render() {
    console.log(this.props.center);

    return (
      <div style={{ height: '30vh', width: '40%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key: 'AIzaSyD4X3Xez83U_L3WZm6Fny8zsSxN_G4s1a4' }}

          defaultCenter={this.props.lat}
          defaultZoom={this.props.zoom}
        >
          {this.state.lat ? 
           <AnyReactComponent
           lat={this.state.lat}
           lng={this.state.lng}
           text="My Marker"
         /> :
         null}

        </GoogleMapReact>
      </div>
    );
  }
}

export default Map;
