import React from 'react'
import Activity from '../components/activity'
import ActivityPage from '../components/activityPage'
import { BrowserRouter as Route } from 'react-router-dom'


const ActivitiesContainer = (props) => {
    function renderActivities() {
        return props.activities.map(activitiy => <Activity activity={activitiy} key={activitiy.id}/>)
    }
    let selectedActivity = props.activities.find(a => a.id === 1)
    return (
        <div>
            <Route exact path='/:id' render={() => <ActivityPage {...selectedActivity}/>}/>
            <Route exact path='/activities' render={() => renderActivities()} />
        </div>
    ) 
}


export default ActivitiesContainer
