    //     this.setState({
    //       searchBar: event.target.value,
    //       searchedtransaction: this.state.transactions.filter(t=> t.description.includes(this.state.searchBar))

    //     })    
    //   }

    //   renderSearch = () => {
    //     this.setState({
    //         searchedActivities: this.state.searchedtransaction
    //     })
    //   }


    // searchActivities = event => {
    //     this.setState({
    //         searchTerm: event.target.value,
    //         searchedActivities: this.state.activities.filter(a => a.name.includes(this.state.searchTerm))
    //         })
    // }

    render() {
        console.log(this.state.length);

        return (
            <div>
                <Search changeSearchInput={this.changeSearchInput} changeButtonState={this.changeButtonState}/>
                <Filter SelectCategory={this.SelectCategory}/>
                <AllActivities renderActivtites={this.renderActivtites} />
            </div>

        )
    }
}

const msp = state => {
    return {
        activities: state.activities
    }
}

export default connect(msp)(ActivitiesPage)


