import React from 'react'
import { connect } from 'react-redux'

class CateogriesContainer extends React.Component{

    // state = {
    //     categories: []
    // }

    componentDidMount(){
    //     fetch('http://localhost:3000/activities').then(resp => resp.json())
    //     .then(data => {
    //         this.setState({categories: data})
    // })
    }

    renderCategories = () => {
        this.props.categories.map(category => <p>{category.name}</p>)
    }

    render(){

        return(
            <div>
                <h1> CateogriesContainer </h1>
                <div> {this.renderCategories()} </div>
            </div>
        )
    }
}

const msp = state => {
    return {
        categories: state.categories,
        activities: state.activities
    }
}

export default connect(msp)(CateogriesContainer)
