import React from 'react'
import { connect } from 'react-redux'
import Activity from '../components/activity'
import { render } from '@testing-library/react'
import { Link } from 'react-router-dom'
import Search from '../components/search'

class ActivitiesPage extends React.Component {

    state = {
        searchTerm: '',
        activities: [],
        searchedActivities: [],
    }

    componentDidMount(){
        this.setState({
            activities: [...this.props.activities]
        })
    }

    filter = () => {

    }

    sort = () => {

    }


    searchActivities = event => {
        this.setState({
            [event.target.name]: event.target.value,
            searchedActivities: this.props.activities.filter(a => a.name.includes(this.state.searchTerm))
        })
    }


    renderActivtites = () => {
        return this.props.activities.map((activity, index) => <Activity index={index} key={activity.id} activity={activity} />)
    }


    render() {
        return (
            <div>
                <Search searchActivities={this.searchActivities} renderSearch={this.renderSearch} />
                <div className='activities'>
                    <h1>ActivitiesPage</h1>
                    {this.renderActivtites()}
                </div>
            </div>

        )
    }
}

const msp = state => {
    return {
        activities: state.activities
    }
}

export default connect(msp)(ActivitiesPage)
