import React from 'react'
import Activity from '../components/activity'
import '../../src/index.css'


class ActivitiesContainer extends React.Component {

    state = {
        startIndex: 0
    }


    changeIndex = () => {
        if (this.state.startIndex >= this.props.activities.length) {
            this.setState({
                startIndex: 0
            })
        } else {
            this.setState({
                startIndex: this.startIndex + 5
            })
        }
    }

    renderActivities() {
        return this.props.activities.slice(this.state.startIndex, this.state.startIndex + 5).map((activitiy, index) => <Activity activity={activitiy} key={activitiy.id} index={index} />)
    }

    render() {


        return (
            <div className='activity-container'>
                {this.renderActivities()}
                <button onClick={this.changeIndex}>Next</button>
            </div>
        )
    }
}


export default ActivitiesContainer
